void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv =  (2.*fragCoord - iResolution.xy) / min(iResolution.x, iResolution.y);

    uv *= 30.*abs(1.1 + sin(iTime*0.1));

    float len;

    for (int i = 0; i < 10; i++) {
    len = length(uv);

        uv.x +=  sin(uv.y + iTime * 0.3)*1.;
        uv.y +=  cos(uv.x + iTime * 1.0 + cos(len * 1.0))*1.;
    }

    len *= cos(len*0.3)+ sin(iTime * 0.3);

    len -= 3.;

    vec3 col = vec3(cos(len + 0.2), cos(len + 0.1), cos(len - 0.05));

    fragColor = vec4(col,1.0);
}
