vec2 dfd(vec2 coord){
  float dx = 1./iResolution.x * 1.0;
  float dy = 1./iResolution.y * 1.0;

  vec2 uv = coord/iResolution.xy;
  vec2 dxUv = vec2(uv.x + dx, uv.y);
  vec2 dyUv = vec2(uv.x, uv.y + dy);

  vec4 color = texture(iChannel0, uv);
  vec4 dxColor = texture(iChannel0, dxUv);
  vec4 dyColor = texture(iChannel0, dyUv);

  float gray = length(color.rgb);
  float dxGray = length(dxColor.rgb) - gray;
  float dyGray = length(dyColor.rgb) - gray;

  return vec2(dxGray, dyGray);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = fragCoord.xy/iResolution.xy;
    vec4 color = texture(iChannel0, uv);
    vec2 dFd = dfd(fragCoord);
	fragColor = step(0.02, length(dFd)) == 0. ? color : color * .5;
    //fragColor = color;
}
