vec2 rotateUV(vec2 uv, float rotation)
{
    float mid = 0.5;
    return vec2(
        cos(rotation) * (uv.x - mid) + sin(rotation) * (uv.y - mid) + mid,
        cos(rotation) * (uv.y - mid) - sin(rotation) * (uv.x - mid) + mid
    );
}

float random (vec2 st) {
    return fract(sin(dot(st.xy,
                         vec2(12.9898,78.233)))*
        43758.5453123);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 color1 = vec3(1.,.3,.1);
    vec3 color2 = vec3(.4,.6,.9);
    vec2 uv = fragCoord/iResolution.xy;
    vec2 uvS = uv;
    uvS.x *= iResolution.x/iResolution.y;
    uvS.x -= 1.-iResolution.y/iResolution.x;

    uvS = uvS * 20.;    

    float corner = random(vec2(floor(uvS.x)+.1*floor(uvS.y)+.2, floor(iTime/1.)));

    //1,3,5,7
    vec2 uvC = rotateUV(fract(uvS),3.14/4.*7.);
    if(corner < .25) {
    	uvC = rotateUV(fract(uvS),3.14/4.);
    } else if(corner < .5) {
        uvC = rotateUV(fract(uvS),3.14/4.*3.);
    } else if(corner < .75) {
        uvC = rotateUV(fract(uvS),3.14/4.*5.);
    }

    vec3 col = vec3(floor(uvC.x*2.));

    fragColor = vec4(mix(color1, color2, col),1.);
    fragColor = vec4(col,1.);
    fragColor = vec4(color1,1.);
    if(col.x > .5) {
    	fragColor = vec4(color2,1.);
    }
    //fragColor = vec4(vec3(corner),1.);
}
