//Display the buffer
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float k=0.9+sin(iTime+cos(iTime*3.0))*0.2;
	fragColor = texture(iChannel0, (uv-0.5)*k+0.5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv=fragCoord / iResolution.xy;
    vec4 old = texture(iChannel0, (uv-vec2(0.5,0.5))*0.99+vec2(0.5,0.5));

    //vec2 pp=iMouse.xy;
    float r=0.3+sin(iTime*0.2)*0.1;
    vec2 pp=(0.3*vec2(sin(iTime*12.0)*0.4+cos(0.3*iTime*(1.0+r)),
                      sin(iTime*12.0)*0.4+sin(0.3*iTime*(2.2+r*3.0)))
                      +0.5)*iResolution.xy;
    float tt=4.0;//iResolution.x/100.0;
    float v1=exp(-length(pp-fragCoord)/tt);
     float v2=exp(-length((iResolution.xy-pp)-fragCoord)/tt);
    float v=(v1+v2)/2.0;
    fragColor = old*0.99+vec4((sin(iTime*5.0)+1.0)*v,(cos(iTime*15.0)+1.0)*v,(sin(iTime*2.0)+1.0)*v,1.0);
}
