void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float scale = iResolution.x/iResolution.y;

    uv = uv.xy - vec2(0.5,0.5);

    vec2 mm = abs(uv)-vec2(0.3,0.3);


    //float rx = mod(abs(uv.x),0.3);
    //float ry = mod(abs(uv.y),0.3);

    float rx = mm.x;
    float ry = mm.y;


    highp float mx = step(0.3, abs(uv.x)); // if 0.4>abs(uv.x)?0:1
    highp float my = step(0.3, abs(uv.y));

    float filletAlpha = 1.0 - mx * my * step(0.2, length(vec2(rx,ry)));  //如果在圆角矩形内，alpha值为1 否则为0。 和0.1做比较 因为圆角半径为0.1



    vec4 color = texture(iChannel0,uv);


    vec4 colorx = vec4(color.r*filletAlpha,color.g*filletAlpha,color.b*filletAlpha,1.0);




    // Output to screen
    fragColor = colorx;
}
