#define PI 3.1415926536

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    float n = noise2(fragCoord * 11.11) * 100.0;
    fragColor = vec4(n,n,n, 1.0);
    vec2 uv = (fragCoord / iResolution.xy - 0.5) 
            * vec2(1.0, iResolution.y / iResolution.x);
    vec3 camera = vec3(10.0*sin(iTime),5,10.0*cos(iTime));
    vec3 look_at = vec3(0);
    vec3 look = normalize(look_at - camera);
    vec3 up_init = vec3(0,1,0);
    vec3 right = cross(up_init, look);
    vec3 up = cross(look, right);
    vec3 ray = normalize(look + (uv.x * right) + (uv.y * up));
    float max_dist = 30.0, dist = 0.0, epsilon = 0.001;
    int max_step = 100, cur_step = 0;
    vec3 where = camera;
    int hit = 0;
    vec3 col = vec3(0);
    for(cur_step=0; cur_step < max_step && dist < max_dist; cur_step++) {
        where = camera + dist * ray;
        vec3 wmod = mod(where + 4.0, 8.0) - 4.0;
        where = max(wmod, where - 4.0); 
        vec3 cr =abs(where)-1.3;
        float dbox = length(max(cr+0.05, 0.0));
        float dbord = length(cr) - 0.1;
        vec3 dedges = vec3(length(cr.yz),length(cr.zx),length(cr.xy))-0.1;
        vec3 dedge3 = max(cr, dedges);
        float dedge = min(dedge3.x, min(dedge3.y, dedge3.z));
        float d = dbox;
        int closest = 1;
        if (dbord < d) {
            d = dbord;
            closest = 2;
        }

        if (dedge < d) {
            d = dedge;
            closest = 3;
        }

        if (d < epsilon) {
            hit = closest;
            break;
        }
        dist += (0.3 * d);
    }
    if (hit == 1) {
        col = vec3(0,0,0);
    } else if (hit == 2){
        col = vec3(1,1,1);

    } else if (hit == 3){
        col = vec3(1,1,1);

    }
    fragColor = vec4(col, 1.0);
}

void mandel( out vec4 fragColor, in vec2 fragCoord )

{
    float mag = 3.3;
    vec2 centre = vec2(-0.74, -0.18);
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord/iResolution.xy - 0.5) * vec2(1, -iResolution.y/iResolution.x) / mag;

    // Time varying pixel color
    float rad = length(uv) * 20.00;
    float ring = floor(fract(rad) *1.1);

    vec2 z = vec2(0,0);
    int i = 0;
    vec2 c = uv + centre;
    for (; i < 1000; i++) {
        if (length(z) > 2.0) {
            break;
        }
        z = vec2(z.x*z.x - z.y*z.y, 2.0 * z.x * z.y) + c;
    }
    float zsmooth = float(i + 1) - log(log(length(z)))/log(2.0);
    float level = float(i % 100) / 100.0; 
    vec3 color = vec3(0);
    if (i < 1000) {
        color = vec3(atan(z.y,z.x)/PI/2.0+0.5, level, level);
    }
    fragColor = vec4(color,1.0);
}
