#define PI 3.141593

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 p = fragCoord/iResolution.xy;

    // Normalise la position du cursor [0;1]
    vec2 cursorPos = iMouse.xy/iResolution.xy;

    //
	vec2 q = p - cursorPos;

    // Couleur du coeur
    vec3 col = vec3(1,0.2,0.2);



    //Variables coeur
    float sizeHeart = 0.2;
    float pulseSize = 0.08;
    float pulseFrequency = 2.0;
	float heightHeart = 1.3;

    //Elargissement du coeur
    q.y = ((1.0/heightHeart)*q.y - abs(q.x) * sqrt(50.0- abs(q.x))/10.0);

    //Pulsation du coeur et taille du coeur
    float r = sizeHeart +pow(abs(pulseSize*sin(PI*pulseFrequency *iTime)),2.0);

    //Cr√©e un flou autour du coeur ( lissage )
    float heart = smoothstep(r, r+0.005,length( q ));

    // Inverse la coloration ( car le noir est rouge et inversement)
    col *= 1.0 - heart;

    // Output to screen
    fragColor = vec4(col,0);
}
