float normpdf(in float x, in float sigma)
{
	return 0.39894*exp(-0.5*x*x/(sigma*sigma))/sigma;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    const int mSize = 11;
    const int kSize = (mSize-1)/2;
    float kernel[mSize];
    vec3 final_color = vec3(0.0);

    //create the 1-D kernel
    float sigma = 20.0;
    float Z = 0.0;
    for (int j = 0; j <= kSize; ++j) {
        kernel[kSize+j] = kernel[kSize-j] = normpdf(float(j), sigma);
    }

    //get the normalization factor (as the gaussian has been clamped)
    for (int j = 0; j < mSize; ++j) {
        Z += kernel[j];
    }

    //read out the texels
    for (int i=-kSize; i <= kSize; ++i) {
        for (int j=-kSize; j <= kSize; ++j) {
            final_color += kernel[kSize+j]*kernel[kSize+i]*texture(iChannel0, (fragCoord.xy+vec2(float(i),float(j))) / iResolution.xy).rgb;

        }
    }

    vec2 uv = fragCoord/iResolution.xy;
    vec4 unblurredColor = texture(iChannel0, uv);

    fragColor = vec4(mix(unblurredColor.rgb, final_color/(Z*Z), unblurredColor.a), 1.0);
}

#define RAY_STEPS 256

const float FOCAL_LENGTH = 16.0;
const float FOCAL_RANGE = 16.0;

const float FOVY = 3.141569 * 0.25;

struct Intersection {
    float t;
    vec3 p;
    vec3 color;
    int object;
};

vec3 rotateY(vec3 p, float a) {
    return vec3(cos(a) * p.x + sin(a) * p.z, p.y, -sin(a) * p.x + cos(a) * p.z);
}

//sphere sdf
float sphere(vec3 p, float r, vec3 c) {
 	return distance(p, c) - r;   
}

//box sdf
float box(vec3 p, vec3 b) {
    return length(max(abs(p) - b, 0.0));
}

//plane sdf
float plane(vec3 p, vec4 n) {
    return dot(p, n.xyz) + n.w;
}

#define LIGHT_POS vec3(0., 7.45, 0.)
#define BACK_WALL_SDF plane(pos, vec4(0.0, 0.0, -1.0, 5.0))
#define LEFT_WALL_SDF plane(pos, vec4(1.0, 0.0, 0.0, 5.0))
#define RIGHT_WALL_SDF plane(pos, vec4(-1.0, 0.0, 0.0, 5.0))
#define CEILING_SDF plane(pos, vec4(0.0, -1.0, 0.0, 7.5))
#define FLOOR_SDF plane(pos, vec4(0.0, 1.0, 0.0, 2.0))
#define TALL_BOX_SDF box(rotateY(pos + vec3(2, 0, -3), 27.5 * 3.14159 / 180.0), vec3(1., 2., 1.))
#define SMALL_BOX_SDF box(rotateY(pos + vec3(-2, 1, -0.75), -17.5 * 3.14159 / 180.0), vec3(1., 1., 1.))

#define BACK_WALL 0
#define LEFT_WALL 1
#define RIGHT_WALL 2
#define CEILING 3
#define FLOOR 4
#define TALL_BOX 5
#define SMALL_BOX 6

void testScene(vec3 pos, out float t, out int obj) {
    t = BACK_WALL_SDF;
    obj = BACK_WALL;

    float t2;
    if ((t2 = LEFT_WALL_SDF) < t) {
        t = t2;
        obj = LEFT_WALL;
    }
    if ((t2 = RIGHT_WALL_SDF) < t) {
        t = t2;
        obj = RIGHT_WALL;
    }
    if ((t2 = CEILING_SDF) < t) {
        t = t2;
        obj = CEILING;
    }
    if ((t2 = FLOOR_SDF) < t) {
        t = t2;
        obj = FLOOR;
    }
    if ((t2 = TALL_BOX_SDF) < t) {
        t = t2;
        obj = TALL_BOX;
    }
    if ((t2 = SMALL_BOX_SDF) < t) {
        t = t2;
        obj = SMALL_BOX;
    }
}

float testScene(vec3 pos) {
 	float t = BACK_WALL_SDF;

    float t2;

    t = min(t, LEFT_WALL_SDF);
    t = min(t, RIGHT_WALL_SDF);
    t = min(t, CEILING_SDF);
    t = min(t, FLOOR_SDF);
    t = min(t, TALL_BOX_SDF);
    t = min(t, SMALL_BOX_SDF);

    return t;
}


vec3 computeNormal(vec3 pos) {
    vec3 epsilon = vec3(0.0, 0.001, 0.0);
    return normalize(vec3(testScene(pos + epsilon.yxx) - testScene(pos - epsilon.yxx),
                          testScene(pos + epsilon.xyx) - testScene(pos - epsilon.xyx),
                          testScene(pos + epsilon.xxy) - testScene(pos - epsilon.xxy))
                     );
}

void march(vec3 origin, vec3 dir, out float t, out int hitObj, vec3 lightPos) {
 	t = 0.001;
    for (int i = 0; i < RAY_STEPS; ++i) {
        vec3 pos = origin + t * dir;
        float m;
        testScene(pos, m, hitObj);
        if (m < 0.01) {
            return;
        }
        t += m;
    }
    t = -1.0;
    hitObj = -1;
}

vec3 computeMaterial(int hitObj, vec3 p, vec3 n, vec3 lightVec, vec3 view) {
    float t;
    float lambert = dot(lightVec, n);
    vec3 mat = vec3(0., 0., 0.);

    switch(hitObj) {
        case BACK_WALL:
        mat = vec3(0.9, 0.9, 0.9);
        break;
        case LEFT_WALL:
        mat = vec3(1., 0., 0.);
        break;
        case RIGHT_WALL:
        mat = vec3(0., 1., 0.);
        break;
        case CEILING:
        mat = vec3(0.9, 0.9, 0.9);
        break;
        case FLOOR:
        mat = vec3(0.9, 0.9, 0.9);
        break;
        case TALL_BOX:
        mat = vec3(1., 1., 1.);
        break;
        case SMALL_BOX:
        mat = vec3(0.8, 0.8, 0.8);
        break;
        case -1:
        mat = vec3(0., 0., 0.);
    }
    return mat * lambert;
}

Intersection sdf3D (vec3 dir, vec3 eye, vec3 lightPos) {
    float t;
    int hitObj;
    march(eye, dir, t, hitObj, lightPos);
    vec3 isect = eye + t * dir;

    vec3 nor = computeNormal(isect);
    vec3 lightDir = normalize(lightPos - isect); 

    vec3 surfaceColor = computeMaterial(hitObj, isect, nor, lightDir, normalize(eye - isect));
    if (hitObj == -1) {
        surfaceColor = vec3(0., 0., 0.);
    }

    return Intersection(t, isect, surfaceColor, hitObj);
}

// returns direction of ray
vec3 rayCast(vec3 eye, vec3 ref, vec2 ndc) {
 	vec3 F = ref - eye;
    vec3 R = normalize(cross(F, vec3(0, 1, 0)));
    vec3 U = normalize(cross(R, F));

    vec3 V = U * length(F) * tan(FOVY * 0.5);
    vec3 H = R * length(F) * tan(FOVY * 0.5) * float(iResolution.x) / iResolution.y;

    vec3 p = ref + ndc.x * H + ndc.y * V;

    vec3 ray = normalize(p - eye);
    ray.x *= -1.; // flip x-axis
    return ray;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
	uv = uv * 2.0 - vec2(1.0, 1.0);

    vec3 eye = vec3(0, 5, -15);
    vec3 ref = vec3(0, 3, 0);

    //vec3 rayDir = rayCast(eye, ref, uv);

    //Intersection isect = sdf3D(rayDir, eye, LIGHT_POS);
    //fragColor = vec4(isect.color, 1.);

    vec3 dir = rayCast(eye, ref, uv);
  	Intersection isect = sdf3D(dir, eye, LIGHT_POS);

    float distAlongCamZ = abs(dot(normalize(ref - eye), (isect.p - eye)));

    float dofZ = min(1.0, abs(distAlongCamZ - (FOCAL_LENGTH + 10. * sin(iTime))) / FOCAL_RANGE);

    fragColor = vec4(isect.color * step(0.0, isect.t), dofZ);
}
