const int MaxIterations = 100;

//Palette function of Inigo Quilez
vec3 palette(float t, vec3 a, vec3 b, vec3 c, vec3 d) 
{
    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 z;
    z.x = 3.0 * (fragCoord.x / iResolution.x - 0.5);
    z.y = 2.0 * (fragCoord.y / iResolution.y - 0.5);

    //Credit for the spiral function goes to vahag1313
	vec2 c = vec2(cos(iTime) * iTime * 0.01, sin(iTime) * iTime * 0.01);

    int i;
    for(i = 0; i < MaxIterations; i++) 
    {
        float x = (z.x * z.x - z.y * z.y) + c.x;
        float y = (z.y * z.x + z.x * z.y) + c.y;

        if((x * x + y * y) > 4.0) break;
        z.x = x;
        z.y = y;
    }

    fragColor =  vec4(palette(float(i)/ float(MaxIterations), vec3(0.5, 0.5, 0.5), vec3(0.5, 0.5, 0.5), vec3(1.0, 1.0, 1.0), vec3(0.30, 0.20, 0.20)), 1.0);
}
