float ln (vec3 p, vec3 a, vec3 b) {
    return length(p-a-(b-a)*dot(p-a,b-a)/dot(b-a,b-a));
}
void mainImage( out vec4 Q, in vec2 U )
{
    Q = A(U);
    vec4 
        E = A(U),
        n = A(U+vec2(0,1)),
        e = A(U+vec2(1,0)),
        s = A(U-vec2(0,1)),
        w = A(U-vec2(1,0));
 	vec3 no = normalize(vec3(e.x*e.x+e.y*e.y-w.x*w.x-w.y*w.y,n.x*n.x+n.y*n.y-s.x*s.x-s.y*s.y,10000.));
    vec3 li = vec3(0,.5,2);
    float o = ln(li,vec3(0),reflect(no,vec3(0,0,1)));
    Q.xyz = .5*(10.*exp(-10.*o*o)+.5*dot(no,normalize(li)))*(0.5+0.5*sin(2.+vec3(1,2,-1)*atan(.1*(E.x*E.x+E.y*E.y+E.z*E.z))));
    Q.w = 1.;
}

void mainImage( out vec4 Q, in vec2 U )
{
    Q = A(U);
    vec4 
        n = A(U+vec2(0,1)),
        e = A(U+vec2(1,0)),
        s = A(U-vec2(0,1)),
        w = A(U-vec2(1,0)),
        m = 0.25*(n+e+s+w),
        l = m-Q;
    Q.z = dt*l.x;
    Q.y += Q.z-k*dt*Q.x*(1.+1./(.01+abs(Q.x)))+q*l.y;
    Q.x += dt*Q.y;

    if (length(U-iMouse.xy)<10.&&iMouse.z>0.)
        Q.x = 1.;
    if(iFrame <1) {
    	Q = vec4(step(10.,length(U-0.5*R)));
    }
}

