void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 R = iResolution.xy;
    vec2 uv = (2.* fragCoord - R) / R.y;
    float pct = 0.;
    float offset = 0.55;

    pct = min(
        distance(
            uv,
            vec2((sin(iTime) * offset) + 0.015,
                 (cos(iTime) * offset)) + 0.15
        ),    
    	distance(
            uv,
            vec2((cos(iTime) * offset) + 0.015,
                 (sin(iTime) * offset)) + 0.15
        )
    );

    bool flip = sin(iTime) - cos(iTime) > 0.;

    pct = smoothstep(flip ? 0.026 : 0.032, 0.031, pct);

    vec3 col = vec3(pct);

    fragColor = vec4(col,1.0);
}
