#define S(a, b, c) smoothstep(a, b, c)
#define PI 3.14159

float sdfBand(float pt, float l, float r, float blur) {
	return S(l - blur, l, pt) - S(r, r + blur, pt);
}

float sdfCircle(vec2 uv, vec2 cd, float r, float blur) {
    vec2 uc = uv-cd;
	float d = dot(uc, uc);
    float r2 = r*r;
    return smoothstep(r2 + blur, r2 - blur, d);
}

float sdfBox(vec2 uv, vec2 bl, vec2 tr, float blur) {
	return sdfBand(uv.x, bl.x, tr.x, blur) * sdfBand(uv.y, bl.y, tr.y, blur);
}


