vec4 pmPainting(vec2 uv) {
    vec3 bg = vec3(0.964706, 0.972549, 0.968627);

    vec4 col = vec4(bg, 1.0);

    uv.y += sin(iTime)*0.1;
    uv.x += cos(iTime - PI/3.0)*0.1;
    vec4 lines = Lines(uv);

    col.rgb = mix(col.rgb, lines.rgb, lines.a);

	return col;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 R = iResolution.xy;
    vec2 uv = fragCoord/R;


    vec4 col = pmPainting(uv);


    fragColor = col;
}
