// variables to specify column and row numbers
float cols = 10.0;
float rows = 8.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	// Normalized pixel coordinates (from 0 to 1)
	vec2 uv = fragCoord/iResolution.xy;

	uv.x *= cols; // change number of columns
	uv.y *= rows; // change number of rows

	// determine whether uv position is even or odd and store it in a variable
	vec3 blackOrWhite = vec3(mod(floor(uv.x) + floor(uv.y),2.0));

    // fract uv to draw multiple tiles
	uv = fract(uv);

	vec3 color;

    // first texture
    vec3 color1 = vec3(texture(iChannel0,uv));
    // second texture
    vec3 color2 = vec3(texture(iChannel1,uv));

   	// background color
    color = color1 * blackOrWhite + color2 * (1.0 - blackOrWhite);

	// Output to screen
	fragColor = vec4(color,1.0);
}
