float instrument(float freq, float t)
{
	float y = 0.;

    y = 0.6*sin(6.28*t*freq)*exp(-0.005*t*freq);
        y += 0.4*sin(2.*6.28*t*freq)*exp(-0.005*t*freq);
	y=pow(y,3.);
    return y;
}

float t(float time, float iTime)
{
	return max(iTime-time,0.);
}

