vec2 hash( vec2 x ) 
{
    const vec2 k = vec2( 0.9183099, 0.2678794 );
    x = x*k + k.yx;
    return -1.0 + 2.0*fract( 16.0 * k*fract( x.x*x.y*(x.x+x.y)) );
}

float noise( in vec2 p )
{
    vec2 i = floor( p );
    vec2 f = fract( p );	
	vec2 u = f*f*(3.0-2.0*f);
    float n =  mix( mix( dot( hash( i + vec2(0.0,0.0) ), f - vec2(0.0,0.0) ), 
                     dot( hash( i + vec2(1.0,0.0) ), f - vec2(1.0,0.0) ), u.x),
                mix( dot( hash( i + vec2(0.0,1.0) ), f - vec2(0.0,1.0) ), 
                     dot( hash( i + vec2(1.0,1.0) ), f - vec2(1.0,1.0) ), u.x), u.y);
    return 0.5 + 0.5*n;
}

float random(vec2 uv)
{
	 return fract(sin(dot(uv.xy, vec2(12.9898,78.233))) * 43758.5453123);
}

float rand(float x)
{
 	return fract(sin(x)*100000.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float base2 = clamp(pow(sin(uv.x*128.), 3.)+0.01, 0.01, 1.);

    float v = floor(uv.x*20.)/20.;
    float a = random(vec2(v))*103.;//position
    float move = a;//rand(floor(uv.x*20.-0.09))*43758.5453123;

    float move2 = rand(floor(uv.x*43758.5453123*(1.-base2)))/10.;

    float base = clamp(pow (sin(uv.y*48.+move+move2), 5.), 0., 1.);

    vec3 Color = (1.-base)*vec3(0.6,1.,0.);

    Color += smoothstep(0.0, 1., base/2.)*vec3(1.,0.9,0.75);

    Color += clamp(base-0.5, 0., 1.)*vec3(1.,0.9,0.75);


    Color -= base2*vec3(10.);

    Color -= Color * noise(uv*128.)/6.;

    // Output to screen
    fragColor = vec4(Color, 1.0);
}
