void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = vec3(0.);

    float s = 50. * sin(uv.x + iTime) + 50.;

    for(float i = 0.; i < s; i++) {     
        vec2 v = uv - i/s;
        v = smoothstep(-.7, .7, v / fwidth(v));

        fragColor += (v.x + v.y) * ( 1.- mod(i,2.)*2.);
    }
}
