float disk(vec2 r, vec2 center, float radius) {
    float d = radius / 20.;
    return 1.0 - smoothstep(radius - d, radius + d, length(r - center));
}

float getFrequency(float x) {
    return texture(iChannel0, vec2(0., x)).x;
}

vec3 getGamingColor(float f) {
    f *= 2.;
    return vec3(sin(f), sin(f *  2.), sin(f * 3.0));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord) {
    vec3 bgCol = vec3(0.);
    vec3 ret = bgCol;

    //int div = (1 << int(iTime / 2.) % 5);
    const int div = 8; // for Safari
    float radius = iResolution.x / float(2 * (div + 2));
    for (int i = 0; i < div; i++) {
        float x = 1. / float(div) * float(i);
        float n = getFrequency(x);
        vec2 center = vec2(iResolution.x / float(div + 1) * float(i + 1), n * iResolution.y);
        //vec3 col = vec3(n, 0., 0.);
        vec3 col = getGamingColor(n);
        ret = mix(ret, col, disk(fragCoord.xy, center, radius));
    }
    vec3 pixel = ret;
    fragColor = vec4(pixel, 1.0);
}
