// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License
//A procedural sun flare
//Made using code from lots of places
//Noise borrowed from IQ: https://www.shadertoy.com/view/Msf3WH
//Polar UV function borrowed from here:https://www.shadertoy.com/view/3tXXz8
//Glowing Spot borrowed from here: https://github.com/cgvirus/Natron-Lens-Flare-Studio/blob/master/GLSL_Lense_Flare_Elements/sun.frag.glsl


#define TileU 1.0f
#define TileV 2.0f
#define M_PI 3.141592

 float globalSize = 1.0; // Global Scale, min=0., max=100.
 float globalRotate= 0.0; // Global Rotation, min=0., max=360.
 float evolution= 1.0; // Evolution, min=1.0, max=360.

//parametres
 float coresize = 3.; // Sun Core Size , min=0., max=100.
 float rotation = .0; // Rotation , min=0.,max=360.
 vec3 SunColor = vec3(0.3,0.25,0.45); // Sun Color


//Procedural Sun Generation
float sun(vec2 uv, vec2 pos, float size)
{
    float rot = radians(rotation+globalRotate)*evolution;
    mat2 m = mat2(cos(rot), -sin(rot), sin(rot), cos(rot));
    uv  = m*uv;
    pos = m*pos;

    vec2 vector = uv-pos;

	float angle = atan(vector.y, vector.x);
	float dist=length(vector); dist = pow(dist,.1);

	float f0 = 1.0/(length(uv-pos)*(1.0/size*100.)/(coresize*globalSize));


    return f0+f0;
}

vec2 polarCoordinates(vec2 uv) {
	vec2 delta = uv - vec2(0.5f);
	float radius = length(delta);
	float angle = atan(delta.y, delta.x) / 2.0f / M_PI;

    radius *= TileU;
    angle *= TileV;

    vec2 polarUV = vec2(radius, angle);
    polarUV = mix(polarUV, uv, 0.01);
	return polarUV;		
}


vec2 hash( vec2 p ) // replace this by something better
{
	p = vec2( dot(p,vec2(127.1,311.7)), dot(p,vec2(269.5,183.3)) );
	return -1.0 + 2.0*fract(sin(p)*43758.5453123);
}

float noise( in vec2 p )
{
    const float K1 = 0.366025404; // (sqrt(3)-1)/2;
    const float K2 = 0.211324865; // (3-sqrt(3))/6;

	vec2  i = floor( p + (p.x+p.y)*K1 );
    vec2  a = p - i + (i.x+i.y)*K2;
    float m = step(a.y,a.x); 
    vec2  o = vec2(m,1.0-m);
    vec2  b = a - o + K2;
	vec2  c = a - 1.0 + 2.0*K2;
    vec3  h = max( 0.5-vec3(dot(a,a), dot(b,b), dot(c,c) ), 0.0 );
	vec3  n = h*h*h*h*vec3( dot(a,hash(i+0.0)), dot(b,hash(i+o)), dot(c,hash(i+1.0)));
    return dot( n, vec3(70.0) );
}

// -----------------------------------------------

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 p = fragCoord.xy / iResolution.xy;
	vec3 mouse = vec3(iMouse.xy/iResolution.xy - 0.5,iMouse.z-.5);

    if(mouse.z < 1.0){
      mouse.x = 0.0;  
      mouse.y = 0.0;         
    }

	mouse.x *= iResolution.x/iResolution.y; //fix aspect ratio    

	vec2 uv = p*vec2(iResolution.x/iResolution.y,1.0);
    uv.x -= 0.4;
    uv -= mouse.xy;
    uv = polarCoordinates(uv);
    uv.y *= 2.0;
    uv.x *= 0.1;


	float f = 0.0;


		uv *= 5.0;
        mat2 m = mat2( 1.6,  1.2, -1.2,  1.6 );
		f  = 0.5000*noise( uv ); uv = m*uv;
		f += 0.2500*noise( uv ); uv = m*uv;
		f += 0.1250*noise( uv ); uv = m*uv;
		f += 0.0625*noise( uv ); uv = m*uv;


	f = 0.5 + 0.5*f;

	vec2 st = fragCoord.xy / iResolution.xy - 0.5;
	st.x *= iResolution.x/iResolution.y; //fix aspect ratio
	float c = sun(st,mouse.xy,1.);

    f*=c;
    f+=c;

    vec3 col = vec3(f);
    col *= SunColor;	
	fragColor = vec4(col, 1.0 );
}
