// variant of "Smooth Winding Number" by Ultraviolet. https://shadertoy.com/view/Wddyz2
// see papers:   https://igl.ethz.ch/projects/winding-number 
//               https://www.dgp.toronto.edu/projects/fast-winding-numbers/

#define ang(p)          atan((p).y, (p).x)
#define angle(p0,p1)  ( mod( ang(p1) - ang(p0) - PI, 2.*PI ) -PI )
#define P(n)            cos(PI*2.*(n+phase)/N + vec2(0,11) ) + .2* hash(n)
#define hash(p)         fract(sin( (p)* vec2(12.9898, 78.233)) * 43758.5453) // https://www.shadertoy.com/view/llySRh

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = 3.* ( u - R*.5)/R.y;

    float  N = 50.,
          PI = 3.1416,
           t = floor( fract(iTime*.2) *N*2. ),
       N_min = max(0.,t-N),
       N_max = min(N ,t),
       phase = 10.*iTime,
           a = 0.;
    for(float i = N_min; i < N_max; i++ ) 
        if ( mod(i,2.) < 1. ) 
            a += angle( P(i-.9) -U, P(i+1.9) - U ) * 2./2.9;
         // a += angle( P(i   ) -U, P(i+1.9) - U ) * 2./1.9;
         // a += angle( P(i   ) -U, P(i+1. ) - U ) * 2./1.;

    O = vec4( .5 + .5* a/6.28  ); 
 // O = vec4(floor(O*16.)/16.);  // see isovalues
    if ( iMouse.z > 0.) {
        if (a < 0.  ) O.b++;
        if (a > 3.14) O.b--;
    }
    if ( fract(iTime/5.) > .5 ) O = ( .6 + .6 * cos( 6.3 * O.r + vec4(0,23,21,0) ) );  // hue. https://www.shadertoy.com/view/llySRh
}
