#define M_PI 3.1415926535897932384626433832795

float plot(vec2 st, float pct){
  // stには座標を渡す
  // pctはyの値。関数f(x)を入れる。
  // 太さ0.04の線が引かれる。
  return  smoothstep( pct-0.01, pct, st.y) -
          smoothstep( pct, pct+0.01, st.y);
}

vec3 grid(vec2 uv, float n) {
  vec3 col = vec3(1., 1., 0)
            *step(0.980, fract(uv.x*n) )
            *step(fract(uv.x*n) , 1.020);
  col += vec3(1., 1., 0)
            *step(0.980, fract(uv.y*n) )
            *step(fract(uv.y*n) , 1.020);
  return col;
}

float n11(vec2 uv) {
  return fract(uv.x*5764.287135 + uv.y*6453.54181) ;
}
float n12(float n) {
  return fract(n*5764.287135 + n*6453.54181) ;
}

vec3 multisin(vec2 uv, float n ) {
  //float i = 1.;
  vec3 col = vec3(0.) ;
  float t = iTime ;
  float alpha = 7494.680418;
  float y = 0.;
  const float s = 1.0/10.;
  float fadeio = abs(cos(t*0.20)*cos(t*0.20)*cos(t*0.20)) ;
  vec3 basecol = vec3(0.55,0,0.6) ;
  for(float i=0.; i < 1. ; i+=s) {
    y = 0.33*exp(i*1.5*n12(i))*sin( uv.x/n*10. + t*0.3*i*0.5 + n12(n) - t*0.2*fract(i*40393.02745) + M_PI*float(int(i/s)/4) )  ;
    col += plot(vec2(uv.x, uv.y+i*0.1), y)
          *(basecol + vec3(0,i/5.*n*.5 ,n*.075) )
          *fadeio ;
  }
  return col;

}

vec3 bgcolor(vec2 uv) {
  float m1 = 0.;
  m1 = clamp(.0, 0.5, uv.y*uv.y );
  return vec3(0.,0., m1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 uv = fragCoord.xy / iResolution.xy ;
  uv -= .5;
  float asp = iResolution.x/iResolution.y;
  uv.x *= asp;

  vec3 col =  vec3(0.);
  col += bgcolor(uv);

  //float y = 2.*uv.x -0.05;
  //col += plot(uv, y );
  col += multisin(uv, 8. );

  fragColor = vec4(col, 1.) ;
}
