float circle(vec2 st,vec2 pos )
{	
	float d=length(st+pos);
    float s=smoothstep(0.5,0.51,d);
	return 1.0- s;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p=(fragCoord.xy*2.0-iResolution.xy)/min(iResolution.x,iResolution.y);
  	float x=2.0*p.y+sin(iTime*5.0);
   	float dist=sin(iTime*10.)*0.1*sin(5.*x)*(-(x-1.)*(x-1.)+1.);
  	p.x+=dist;
    float r=circle(p-vec2(0,dist)*0.3,vec2(0.));
    float g=circle(p+vec2(0,dist)*0.3,vec2(0.));
    float b=circle(p+vec2(dist,0.)*0.5,vec2(0.));
    fragColor=vec4(r,g,b,1.);

}
