    //return fract(sin(dot(uv, vec2(12.9898,78.233)))*10000.*iTime);
	//return fract(sin(dot(uv, iMouse.xy))*10000.);
	//return (fract(sin(dot(uv, vec2(12., 70.)))*100000.));
    return (fract(sin(dot(uv, vec2(12., 70.)))*43758.5453123));
}

// Value noise
float noise(in vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);

    // Four corners in 2D of a tile
    float a = rand(i);
    float b = rand(i + vec2(1.0, 0.0));
    float c = rand(i + vec2(0.0, 1.0));
    float d = rand(i + vec2(1.0, 1.0));

    // Smooth Interpolation

    // Cubic Hermine Curve.  Same as SmoothStep()
    vec2 u = f*f*(3.0-2.0*f);
    // u = smoothstep(0.,1.,f);

    // Mix 4 coorners porcentages
    return mix(a, b, u.x) + 
            (c - a)* u.y * (1.0 - u.x) + 
            (d - b) * u.x * u.y;
}

vec3 blue = vec3(0.,128./255.,1.);
vec3 black = vec3(0.,0.,0.);
vec3 white = vec3(1.,1.,1.);

vec3 rgb(float r, float g, float b) 
{
	return vec3(r / 255.0, g / 255.0, b / 255.0);
}

vec4 rectangle(vec2 uv, vec2 pos, float width, float height, vec3 color) {
	float t = 0.0;

	if ((uv.x > pos.x - width / 2.0) && (uv.x < pos.x + width / 2.0)
		&& (uv.y > pos.y - height / 2.0) && (uv.y < pos.y + height / 2.0)) {
		t = 1.0;
	}
	return vec4(color, t);
}

mat2 scale2d(vec2 value)
{
    return mat2(value.x,0.,0.,value.y);
}
mat2 rotate2d(float angle)
{
    return mat2(cos(angle), - sin(angle),sin(angle),cos(angle));
}
