	uv = fract(uv * 5.);
	// Rectangle
	vec3 col = rgb(255.0, 255.0, 0.0);
	vec4 rect = rectangle(uv, center, width, height, col);
	float c = fract(sin(uv.x * 100.) * 5647.);
    float c2 = fract(sin(uv.y * 100.) * 5647.);

    vec3 cols = vec3(c);

    green -= vec3(c);
    blue -= vec3(c2);
    red -= vec3(c);
