void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-iResolution.xy*0.5)/iResolution.y;

    float mask = smoothstep(0.5, 0.0, length(uv));
    mask *= 1.0-(uv.y+0.5);

    float f = 10.0;
    float newTime = iTime*3.0;
    float d = (uv.y+0.5)+1.0; // denser higher
    float final = 0.05*sin(dot(uv, vec2(sin(newTime*0.2),cos(newTime*0.15)))*10.5*d+newTime);
    final += 0.15*sin(dot(uv, vec2(sin(newTime*0.2+1.42),cos(newTime*0.15+1.46)))*6.5*d*d+newTime);
    final += 0.15*sin(dot(uv, vec2(sin(newTime*-0.2+2.42),cos(newTime*-0.2+2.42)))*20.5+newTime);
    final += 0.09*sin(dot(uv, vec2(sin(newTime*0.26+2.42),cos(newTime*0.26+2.42)))*16.5+newTime);

    final = final*0.5+0.5;
    final *= mask;
    final += mask*0.7;

    final += smoothstep(0.5, 0.6, final);

    //final = mask*10.0;

    // Output to screen
    fragColor = vec4(vec3(final*0.4, final*0.6, final*0.8),1.0);
}
