void mainImage(out vec4 o, vec2 u)
{
    vec2 U = (u - .5*iResolution.xy) / iResolution.y;
    for(float i = 3. + texture(iChannel1, U).x/4.; i <= 20.;)
    {
        o = texture(iChannel0, (normalize(vec3(U, 1))*i + iTime)/30.);
        if (o.a <= .1)
        {
            o.rgb /= length(o.rgb)*i/5.;
            break;
        }
        i += i/200.;
    }
}
