void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
	vec2 sqs = vec2(0.03, 0.05);

    uv.x += sin(uv.y*3.0+iTime)*0.01;
	uv.y += sin(uv.x*3.0+iTime)*0.01;

    int win = int(floor(uv.x/sqs.x));
    int hin = int(floor(uv.y/sqs.y));
    vec3 col = vec3(0.0, 0.0, 0.0);

    if ((hin+win)%2==0){
		col = vec3(sqs.x*float(win), sqs.y*float(hin), 1);
    } else {
        col = vec3(1, sqs.x*float(win)+0.3, sqs.y*float(hin));
    }

    fragColor = vec4(col,1.0);
}
