#define CAM_POS vec3(3,1,1)
#define LIGHT_DIR vec3(1., 1., -1.)
#define saturate(x) min(1., max(0., (x)))

//maths
vec4 plane_decompose(vec3 point, vec3 normal) {
    float d = dot(point,normal);
    return vec4(point-normal*d, d);
}

float pulse(float x)
{
    x = saturate(x);
    x -= 0.5;
    x = x * 2.;
    return 1.0 - x*x*(3.0-2.0*x);
}

//colors
const mat3 YUV_to_RGB = mat3(
         1.0,1.0,1.0,
        0.0,-0.39465,2.03211,
        1.13983,-0.58060,0.0);

const mat3 XYZ_to_RGB = mat3(
    3.2406, -0.9689, 0.0557,
    -1.5372, 1.8758, -0.2040,
    -0.4986, 0.0415, 1.0570);

const vec3 D65_XYZ = vec3(.95047, 1.0, 1.08883);
const vec3 D65_xyY = vec3(0.31271, 0.32902, 1.0);
const vec3 D65_Yuv = vec3(1.0, 0.19784, 0.46834);
const vec3 ONE = vec3(1.0,1.0,1.0);

vec3 XYZ_to_xyY(vec3 XYZ) {
 	float inv_sum = 1.0/dot(XYZ,ONE);
    return XYZ.xyy * vec3(inv_sum,inv_sum,1.0);
}

vec3 xyY_to_XYZ(vec3 xyY) {
 	float s = xyY.z/xyY.y;
    float z = 1.0-dot(vec2(1.0,1.0), xyY.xy);
    return vec3(xyY.x, xyY.z, z) * vec3(s,1.0,s);
}

vec3 LUV_to_XYZ(vec3 LUV) {
    vec2 uv = LUV.yz / (13.0*LUV.x) + D65_Yuv.yz;
    float Y = LUV.x > 8.0 ? 
        D65_Yuv.x * pow((LUV.x+16.0)/116.0, 3.0) : 
    	LUV.x * D65_Yuv.x * 0.00110705645;
    float X = Y * 9.0 * uv.x / (4.0*uv.y);
    float Z = Y * (12.0-3.0*uv.x-20.0*uv.y)/(4.0*uv.y);
    return vec3(X,Y,Z);
}

vec3 YUV_spiral(float v, float r, float l, float L, float h, float H) {
    float hue = mix(h,H,v);
    float lum = mix(l,L,v);
    float amp = r*lum*(1.0-lum);
 	float a = 3.14159*2.0*(hue);
    vec3 c = vec3(lum,sin(a),cos(a));
   	c.yz *= amp;
    return YUV_to_RGB*c;
}

vec3 LUV_spiral(float v, float r, float l, float L, float h, float H) {
    float hue = mix(h,H,v);
    float lum = mix(l,L,v);
    float amp = r*lum*(1.0-lum);
 	float a = 3.14159*2.0*(hue + iTime * 0.1);
    vec3 c = vec3(lum,sin(a),cos(a))*100.0;
   	c.yz *= amp;
    return XYZ_to_RGB*LUV_to_XYZ(c);
}


// signed distance stuff
float sd_sphere(vec3 pos, vec3 c, float r) {
 	return length(c-pos)-r;   
}

float sd_plane(vec3 pos, vec3 normal, vec3 point) {
	return dot(normal, pos-point);    
}

float sd_torus(vec3 pos, vec3 mid, vec3 axis, float radius, float thickness) {
 	pos -= mid;
    vec4 d = plane_decompose(pos, axis);
    return length(vec2(length(d.xyz)-radius, d.w)) - thickness;
}

float sd_union(float a, float b) {
 	return min(a,b);   
}

float sd_intersect(float a, float b) {
 	return max(a,b);   
}

//mixing params
float sd_union_mix(float a, float b) {
 	return a < b ? 0. : 1.;   
}

float sd_intersect_mix(float a, float b) {
 	return a > b ? 0. : 1.;   
}
vec2 sd_smooth_mix(float a, float b, float k) {
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    float d = mix( b, a, h ) - k*h*(1.0-h);
    return vec2(d,1.0-h);
}

// hit/material stuff
struct Hit {
 	float dist;
    vec3 albedo;
    float wrap;
    float smoothness;
    float metallic;
    float spec; // f0 = t*0.8
};

Hit default_hit() {
	Hit h;
    h.dist = 1000.;
    h.albedo = vec3(0,0,0);
    h.wrap = 0.;
    h.smoothness = 0.;
    h.metallic = 0.;
    h.spec = 0.5;
    return h;
}

Hit lerp(Hit a, Hit b, float x) {
    float y = 1.0-x;
    Hit c;
    c.dist = a.dist*y + b.dist*x;
    c.albedo = a.albedo*y + b.albedo*x;
    c.wrap = a.wrap*y + b.wrap*x;
    c.smoothness = a.smoothness*y + b.smoothness*x;
    c.metallic = a.metallic*y + b.metallic*x;
    c.spec = a.spec * y + b.spec * x;
    return c;
}

Hit hit_union(Hit a, Hit b) {
 	return lerp(a,b,sd_union_mix(a.dist,b.dist));   
}

Hit hit_smooth(Hit a, Hit b, float k) {
    vec2 sd = sd_smooth_mix(a.dist,b.dist, k);
    Hit h = lerp(a,b,sd.y);
    h.dist = sd.x;
 	return h;
}

// lighting maths

float wrap_diffuse(float ndotl, float wrap) {
    float div = (1.+wrap)*(1.+wrap);
 	return saturate((ndotl+wrap)/div);
}

// visibility = 0.25*geometry/(ndotl*ndotv)
float visibility(float ndotl, float ndotv, float roughness) {
    float rr = roughness * roughness;
    float ll = ndotl * sqrt((-ndotl*rr+ndotl)*ndotl+rr);
    float lv = ndotv * sqrt((-ndotv*rr+ndotv)*ndotv+rr);
    return 0.5 / (ll+lv+1e-6);
}

float ndf(float ndoth, float roughness) {
 	float d = (ndoth  * roughness - ndoth) * ndoth + 1.;
    return roughness / (d*d+1e-7) / 3.14195;
}

vec3 fresnel(vec3 f0, float ndoth) {
 	float t = (1.-ndoth);
    t = t*t*t*t*t;
    return f0 + (1. - f0)*t;
}

vec3 fresnelMix(vec3 f0, vec3 f90, float ndotv) {
 	float t = (1.-ndotv);
    t = t*t*t*t*t;
    return mix(f0, f90, t);
}

vec3 specular(float ndotl, float ndotv, float ndoth, vec3 f0, float smoothness) {
    float roughness = 1.-smoothness;
    roughness *= roughness;
 	float D = ndf(ndoth, roughness);
    float V = visibility(ndotl, ndotv, roughness);
    vec3 F = fresnel(f0, ndoth);

    return D*V*F;
}


//materials

Hit gold(float sd) {
  Hit h = default_hit();
  h.albedo = vec3(1.0, 0.835, 0.5);
  h.smoothness = 0.99;
  h.metallic = 1.0;
  h.dist = sd;
  return h;
}

Hit silver(float sd) {
  Hit h = default_hit(); 
  h.albedo = vec3(1.0, 1.0, 1.0);
  h.smoothness = 0.99;
  h.metallic = 1.0;
  h.dist = sd;  
  return h;
}

Hit obsidian(float sd) { 
  Hit h = default_hit();
  h.albedo = vec3(0.0, 0.0, 0.0);
  h.smoothness = 0.99;
  h.metallic = 0.0;
  h.spec = 0.8;
  h.dist = sd;
  return h;
}

Hit emerald(float sd) {
  Hit h = default_hit();
  h.albedo = vec3(0.3, 1.0, 0.2);
  h.smoothness = 0.1;
  h.metallic = 1.0;
  h.spec = 0.5;
  h.dist = sd;
  return h;
}

float ring(vec3 pos, float sp, float t0, float t1) {
  t0 *= 2.*3.14159;
  t1 *= 2.*3.14159;

  float a = atan(pos.z, pos.x)* sp + t1;
  vec2 p0 = vec2(sin(a), cos(a)) * 0.5;
  return sd_torus(pos, vec3(0,p0.x,0), vec3(0,1,0), 1. + p0.y, 0.05);
}
Hit scene(vec3 pos) {
  float t = iTime * 0.2;

  // outer ring
  float rings = 3.;
  Hit ra = default_hit();
  for(float i = 0.; i < rings; ++i) {
    Hit r0 = silver(ring(pos, 1., fract(t*0.2), fract(-t) + i/rings));
    ra = hit_union(ra, r0);
  }

  Hit rb = default_hit();
  for(float i = 0.; i < rings; ++i) {
    Hit r0 = gold(ring(pos, 1., fract(t), fract(t) + i/rings));
    rb = hit_union(rb, r0);
  }

  Hit r = hit_smooth(ra,rb, 0.1);
  Hit h = r;

  // sphere
  Hit s = obsidian(sd_sphere(pos, vec3(0,0,0), 1. + pulse(fract(t*6.)+0.5) * 0.05));
  h = hit_smooth(h,s, 0.1);


  return h;
}

float sd_scene(vec3 pos) {
 	return scene(pos).dist;   
}

vec3 normal_scene(vec3 pos) {
	vec2 e = vec2(0.00001, 0.0);
    return normalize(vec3(
       sd_scene(pos+e.xyy) - sd_scene(pos-e.xyy),
       sd_scene(pos+e.yxy) - sd_scene(pos-e.yxy),
       sd_scene(pos+e.yyx) - sd_scene(pos-e.yyx)
    ));
}


vec3 sky(vec3 dir, float lod) {
    //this is awful. really want mipmaps here
    vec3 h = texture( iChannel0, dir).xyz;
    vec3 l = texture( iChannel1, dir).xyz;
    if(lod < 0.5) return mix(h,l,lod*2.);
    else return mix(l, vec3(1,1,1) * 0.5, lod*2.-1.);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy - iResolution.xy*0.5) / iResolution.yy;

    float time = iTime * 2. * 3.15159 * 0.05;
    vec3 pos = vec3(sin(time), 0.3, cos(time)) * 3.0;
    vec3 focus = vec3(0,0,0);
    vec3 fwd = normalize(focus-pos);
    vec3 up = vec3(0,1,0);
    vec3 right = normalize(cross(up,fwd));
    up = normalize(cross(fwd,right));
    vec3 dir = normalize(right*uv.x + up*uv.y + fwd);


    float d = 0.0;
    for(int i = 0; i < 512; ++i) {
        vec3 p = pos+dir*d;
        float sd = sd_scene(p);
        if(abs(sd) < 0.001 || d > 200.0) break;
        d += sd * 0.9;
    }

    Hit hit = scene(pos+dir*d);
    if(hit.dist > 0.1) {
        float c = 0.9;
        vec3 bgc = LUV_spiral(uv.y + 0.5, 1.2-length(uv)*1.2, 0.3, 0.9, c, c+0.2);
        fragColor = vec4(bgc,1);
        return;
    }
    vec3 N = normal_scene(pos+dir*d);
    vec3 L = normalize(LIGHT_DIR);
    vec3 V = -dir;
    vec3 H = normalize(V+L);
    vec3 R = reflect(dir,N);


    float ndotl = dot(N,L);
    float ndotv = saturate(dot(N,V));
    float ndoth = saturate(dot(N,H));

    float diff = wrap_diffuse(dot(N,L), hit.wrap)/3.14159;
    diff *= (1.-hit.metallic);

    float f0d = hit.spec * 0.8;

    vec3 specColor = mix(vec3(f0d,f0d,f0d), hit.albedo, hit.metallic);
    vec3 spec = specular(saturate(ndotl), ndotv, ndoth, specColor, hit.smoothness);
    spec *= ndotl * 3.14195;
    spec = max(vec3(0,0,0),spec);


    float proughness = 1.0-hit.smoothness;
    float mip = proughness;
    vec3 refl = sky(R,mip);
    float roughness = proughness * proughness;
    vec3 grazingColor = vec3(1,1,1) * saturate(hit.smoothness + mix(f0d, 1.0, hit.metallic));
    refl *= 1.0 / (roughness*roughness+1.0);
    refl *= fresnelMix(hit.albedo, grazingColor, ndotv);

    fragColor = vec4(hit.albedo * diff + spec + refl,1);
}
