precision highp float;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 p=vec3(fragCoord.xy/5e2-.5,mod(iTime,.8));
    for(int i=0;i<5;++i){
        p = abs(p)/dot(p,p)-sound(iTime)*2.;
        p.zy=vec2(p.z*p.z-p.y*p.y,2.*p.z*p.y);
        p.zxy=p.xyz;
    }
    fragColor=vec4(exp(-dot(p,p)*32.));
}

vec2 mainSound( in int samp, float time )
{
    return vec2(sound(time));
}
