void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float halfX = iResolution.x / 2.0;
    float halfY = iResolution.y / 2.0;

    if ((fragCoord.x < halfX) && (fragCoord.y > halfY)) {
      // Top left quadrant: black/white every other frame.
      fragColor = vec4(((iFrame & 1) == 1) ? 1.0 : 0.0);
    } else {
      // Remaining 3 quadrants: on click, intensity proportional
      // to X position (left = black, right = white)
      fragColor = vec4(iMouse.x / iResolution.x);
    }
}
