float remap(float a,float b,float p){
	return (p-a)/(b-a);
}
float N21(vec2 p){
	p=fract(p*vec2(22435.54,8926.44));
    p+=dot(p,p+23.424);
    return fract(p.x*p.y);
}
vec2 N22(vec2 p){
	float n=N21(p);
    return vec2(n,N21(p+n));
}
float Blob(vec2 center,vec2 uv){
    center+=sin(iTime*2.*N22(center));
	float d=length(uv-center);

    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
	uv*=3.;
    // Time varying pixel color
    vec3 col =vec3(0.);
	float num=3.;
 	float blob=0.;


    for(float i=0.;i<1.;i+=1./num){
    blob+=1.*N21(vec2(i+.3,i+.1))/Blob(vec2(i+.3,i+0.1),uv);
    }
	blob=remap(0.,3.,blob);
     //col=vec3(blob,.52,.9);
    col+=blob*vec3(blob,.52,.65)*.8;
    // Output to scre9en
    fragColor = vec4(col,1.0);
}
