float pattern(vec2 uv, float k){
    return smoothstep(abs(sin((length(vec2(sin(uv.x * (sin(iTime * 0.5) + 1.2) * 5.), sin(uv.y * (sin(iTime * 0.5) + 1.2) * 5.))) - cos(iTime * 0.4)) * (sin((iTime) * 0.1) * 10. + 1.))), abs(sin(iTime) * 0.9), k);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


	vec2 uv = (fragCoord - iResolution.xy * 0.5)/iResolution.y;

    vec3 col = vec3(pattern(uv, 1.), pattern(uv - vec2(0.005, 0.0), 1.), pattern(uv + vec2(0.005, 0.0), 1.));


    fragColor = vec4(col,1.0);
}
