void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    vec3 barycentric = vec3(0.0);
    ivec2 sss = ivec2(0, 1070141392);
  	//barycentric.yz = intBitsToFloat(ray_intersection_data.yz);
	barycentric.yz = intBitsToFloat(sss);
	//barycentric.z = 1.57079;

    barycentric.x = 1.0 - barycentric.y - barycentric.z;

    vec2 spher = barycentric.yz;
    col = vec3(sin(spher.y) * cos(spher.x), sin(spher.y) * sin(spher.x), cos(spher.y));

    // Output to screen
    fragColor = vec4(col,1.0);
}
