void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord - iResolution.xy * 0.5)/iResolution.y;

    float squares;
    float zoomout = sin(length(uv) * cos(iTime * 0.1) * 5.) * sin(iTime * 0.1) * 30.;

    if (int(uv.x * (sin(iTime) + 1.) * 0.5 * zoomout) % 2 == 0) {
        squares = 1. * (uv.x / abs(uv.x));
    } else {
        squares =  1. * -(uv.x / abs(uv.x));
    }

    if (int(uv.y * (cos(iTime) + 1.) * 0.5 * zoomout) % 2 == 0) {
        if(uv.y > 0.){
            squares = -squares;
        }
    } else {
        if(uv.y < 0.){
            squares = -squares;
        }
    }
    vec3 col = vec3(squares);

    // Output to screen
    fragColor = vec4(col,1.0);
}
