void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv =2.0*(
        (fragCoord/iResolution.xy)-vec2(0.5))
    	*vec2(iResolution.x/iResolution.y,1.0);
    vec2 UV = fragCoord/iResolution.xy;

    vec2 p1 = vec2(0.5*sin(iTime*1.1), 0.8*cos(iTime*1.3));
    vec2 p2 = vec2(0.6*sin(iTime*1.2), 0.7*cos(iTime*1.4));
    vec2 p3 = vec2(0.7*sin(iTime*1.3), 0.6*cos(iTime*1.5));
    vec2 p4 = vec2(0.8*sin(iTime*1.4), 0.5*cos(iTime*1.6));

    float l1=length(uv -p1);
    float l2=length(uv -p2);
    float l3=length(uv -p3);
    float l4=length(uv -p4)*0.2;


    vec3 col1=vec3(l1,0,0);
    vec3 col2=vec3(0,l2,0);
    vec3 col3=vec3(0,0,l3);
    vec3 col4=vec3(l4);

    vec3 crt=texture(iChannel0, UV).rgb;


    vec3 outcol=mix((1.0 -(col1 + col2 + col3) * 10.0*col4),crt,0.5);
    // Output to screen
    fragColor = vec4(outcol,1.0);
}

