float smooth_circle(vec2 coords, vec2 center, float rad)
{
    float d = distance(coords, center);
    float pw = 1./iResolution.y;
    return smoothstep(rad,rad-pw,d);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord.xy - .5*iResolution.xy)/ (iResolution.y);
    vec3 col = vec3(0);

    vec2 center = vec2(0);
    float rad = .3;

    if(dot(normalize(uv), vec2(1,0)) < .8+mix(0.,.2,abs(cos(iTime*5.))))
    {
		col = vec3(1,1,0)*smooth_circle(uv, center, rad);
    }

	col -= vec3(1)*smooth_circle(uv, vec2(-.08,0.14), .05);

    for (int i=0; i<3; ++i)
    {
       col += vec3(1,1,0)*smooth_circle(uv, vec2(1.- float(i)*.4 - 0.001*float(int(iTime*500.) % 397) , 0), .05);
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}
