float mandelbrot(vec2 uv)
{
    vec2 c = uv;
    vec2 z = uv;
    float maxSteps = 150.0;
    float steps = 0.0;
    for (steps = 0.0; steps<=maxSteps; ++steps)
    {
        z = vec2(z.x*z.x - z.y*z.y, 2.0*z.x*z.y) + c;
        if (length(z) > 2.0) break;
    }
    return steps / maxSteps;
}

float mandelbrotImg(vec2 uv)
{
    vec2 c = uv;
    vec2 z = uv;
    float maxSteps = 150.0;
    float steps = 0.0;
    for (steps = 0.0; steps<=maxSteps; ++steps)
    {
        z = vec2(z.x*z.x - z.y*z.y, 2.0*z.x*z.y) + c;
        if (2.0*z.x*z.y > 1000.0) break;
       // if (length(z) > 2.0) break;
    }
    return steps / maxSteps;
}

vec2 mandelbrotVec2(vec2 uv)
{
    vec2 c = uv;
    vec2 z = uv;
    float maxSteps = 150.0;
    float steps = 0.0;
    for (steps = 0.0; steps<=maxSteps; ++steps)
    {
        z = vec2(z.x*z.x - z.y*z.y, 2.0*z.x*z.y) + c;
        //if (2.0*z.x*z.y > 1000.0) break;
        if (length(z) > 2.0) break;
    }
    return z;
}
vec2 mandelbrotImgVec2(vec2 uv)
{
    vec2 c = uv;
    vec2 z = uv;
    float maxSteps = 150.0;
    float steps = 0.0;
    for (steps = 0.0; steps<=maxSteps; ++steps)
    {
        z = vec2(z.x*z.x - z.y*z.y, 2.0*z.x*z.y) + c;
        if (2.0*z.x*z.y > 1000.0) break;
        //if (length(z) > 2.0) break;
    }
    return z;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv *= 2.5/exp(iTime/50.0);
    uv += vec2(-0.406, 0.293);
    uv.x *= iResolution.x / iResolution.y;

    float mr = mandelbrot(uv);
    float mi = mandelbrotImg(uv);
    vec2 mvr = mandelbrotVec2(uv);
    vec2 mvi = mandelbrotImgVec2(uv);

    float vr = sqrt(sqrt(sqrt(abs(mr))));      // imaginating the imaginary
    vr = pow(abs(vr), fract(iTime*.5)*2.+.5);
  	vr = vr / (1.-vr);

    float vi = sqrt(sqrt(sqrt(abs(mi))));      // imaginating the hyper-imaginary  ^_^
    vi = pow(abs(vi), fract(iTime*.5)*2.+.5);
    vi = vi / (1.-vi);

    vec3 c = vec3(cos(vi),cos(vi),cos(vi)*cos(vr)); // join (vi , vr)

    float mc = 1.-step(1.0, mr); 	// exists solution?
    c *= mc; 						// remove nonexistent

    // imagining the nonexistent
    {
        c += vec3(cos(length(mvi)*200.+iTime))*(1.-mc);
        //c -= vec3(cos(length(mvr)*200.+iTime))*(1.-mc); //  real - imaginary == null

        c -= vec3( (cos(length(mvi)*30.+iTime*2.))   )*(1.-mc)*vec3(1.,0.,0); // color animation
    }


    // Output to screen
    fragColor = vec4(c,1.0);
}
