float Get(int index, int radix)
{
    float result = 0.0;
    float fraction = 1.0 / float(radix);


    while (index > 0)
    {
        result += float(index % radix) * fraction;

        index /= radix;
        fraction /= float(radix);
    }

    return result;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy;
    float c1 = Get(int(uv.x) & 7,2);
    float c2 = Get(int(uv.x) & 7,3);

    // Output to screen
    fragColor = vec4(c1,c2,0.0,1.0);
}
