// Nodevember 2020 challenge
// Day 30
// Theme: pattern / illusion
//
// Twitter @lopoisaac

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    const float PI = 3.1416;

    vec2 waves, tiles;
    float col, lines, slices; 

    // 12 columns, 10 rows
    tiles = vec2(12., 10.);

    // displace uvs horizontally
    slices = floor(uv.y * tiles.y) / tiles.y;
  	uv.x += sin(slices * 7. + iTime * 0.2) * 0.03;

    // make wave shapes
    waves = sin(uv * tiles * PI);

    // white lines
    lines = step(0.9, fract(uv.y * tiles.y));

    // mixing wave shapes and white lines
    col = max(waves.x, lines);

    fragColor = vec4(vec3(col),1.0);
}
