void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    //distortion factor
    float k = 1.0;

    //normalization factor
    float n = 0.5;

    //shift uv to [-0.5; 0.5]
    uv -= vec2(0.5);
    //apply distortion
    float l = length(uv);
    uv *= (1.0+k*l*l);
    //normalize
    //simplifies to 1+0.25*k without parametrization
    uv /= (1.0+k*n*n);
    //shift uv back to [0.0; 1.0]
    uv += vec2(0.5);

    //extra code to replace samples out of bounds with black
    vec4 color = vec4(0.0);
    if(uv.x >= 0.0 && uv.x <= 1.0 && uv.y >= 0.0 && uv.y <= 1.0)
        color = texture(iChannel0,uv);

    // Output to screen
    fragColor = color;
}
