const int BALLS = 12;  // go for 

const vec2 G = vec2(32., 32.);
float S = .5;

vec2 sphereMap(vec3 p) {
	return vec2(
    	.5 + atan(p.z, p.x) / (2.*3.14159),
		.5 - asin(p.y) / 3.14159
    );
}

mat2 Rot(float a) {
    float s = sin(a), c = cos(a);
    return mat2(c, -s, s, c);
}

float cs(vec3 p, vec2 rot) {
    p.xy *= Rot(rot.x);
    //p.yz *= Rot(rot.y);

    vec2 cc1 = sphereMap(p);

    float 
        c = 0.;
    	c += smoothstep(S, 1., abs(fract(cc1.x * G.x) * 2. - 1.));
    	c *= smoothstep(S, 1., abs(fract(cc1.y * G.y) * 2. - 1.));

    return c * c * (3. - 2. * c);
}

vec4 drawBalls(vec3 ro, vec3 rd) {
    vec3 p;
    vec3 col = vec3(0.);
	float r = .75;

    ro.xz *= Rot(iTime);
	rd.xz *= Rot(iTime);    

    float t1 = 1e3;
    for (int i = 0; i < BALLS; i++) {        
        float fi = float(i);

        // ball pos
        vec3 s = vec3(
            sin(fi * 323.34) * 4., 
            -abs(sin(iTime * 2. + fi)) * 2. + r, 
            cos(fi * 234.56) * 4.
        );

        float t = dot(s - ro, rd);

        p = ro + rd * t;

        float y = length(s - p);

        if (y < r) {
            float x = sqrt(r * r - y * y);
            vec2 rot = vec2(-iTime + fi, cos(-fi -iTime));

            col += cs(ro + rd * (t - x) - s, rot) * .75; 
            col += cs(ro + rd * (t + x) - s, rot) * .5;

            if (col.x > .0) break;
        }
    }

    return vec4(col, t1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 U = (2. * fragCoord - iResolution.xy ) / iResolution.y;

    vec3 col = vec3(.3, 0.1, .0) + smoothstep(1., .0, abs(U.y)) * .1 ;

    vec3 ro = vec3(0., 1., 10.);
    vec3 rd = normalize(vec3(U.x * .3, U.y * .3 - .01, 1.)); 

    float rot = iMouse.x / iResolution.x * 3.14 * 2.;

    ro.xz *= Rot(rot);
	rd.xz *= Rot(rot);    

    col += drawBalls(ro, rd).rgb;

    // floor
    float h = -ro.y / rd.y;
    if (h > 0.) {
        col -= .02;        
        rd.y *= -1.;
        S = 0.;

        col += drawBalls(vec3(ro.x, -ro.y + 3., ro.z), rd).rgb * .04;        
    }

    fragColor = vec4(col / max(.05, smoothstep(.0, .02, length(rd.xy - vec2(.5, 0.)) / 12.)), 1.0);
}
