void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float DitherFactor = abs(sin(iTime*1.0));

    if (abs(DitherFactor) > .001)
	{
		float RandCos = cos(dot(floor(fragCoord), vec2(347.83451793,3343.28371963)));
		float RandomVal = fract(RandCos * 1000.0);
		float RetVal = 0.0; //(DitherFactor < 0.0) ? 
        if(DitherFactor < RandomVal)
        {
            RetVal = 1.0;
        }

        fragColor = vec4(RetVal);
	}

}
