float W = 320.0f;
float H = 240.0f;

float degrade(float p, float c)
{
	return ceil(p * c) / c;
}


vec2 virt_scale(vec2 p)
{
	p.x = degrade(p.x, W);
    p.y = degrade(p.y, H);

    return p;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    uv = virt_scale(uv);

   	uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;


    if (fragCoord.y > (iResolution.y / 2. - 20.0)) {
        float depth = degrade(uv.y, float(1 << 4));
        fragColor = vec4(vec3(1.0 - depth, 0.8 - depth + 0.8, 1.0), 1.0);
        return;
    }

    vec3 pl = vec3(uv, 0.0);

    float t = radians(90.0);

    float t2 = iTime * 0.5;

    pl.yz = vec2(pl.y, 1.0) * mat2(cos(t), -sin(t), sin(t), cos(t));

    vec2 s = pl.xy / pl.z;

    s += vec2(1.0) * mat2(cos(t2), -sin(t2), sin(t2), cos(t2));

    vec4 tx = texture(iChannel0, s);

    float d = float(2 << 1);

    tx.x = degrade(tx.x, d);
    tx.y = degrade(tx.y, d);
    tx.z = degrade(tx.z, d);

    fragColor = tx;
}
