// https://iquilezles.org/articles/distfunctions2d
float sdBox( in vec2 p, in float b )
{
    vec2 d = abs(p)-b;
    return length(max(d,vec2(0))) + min(max(d.x,d.y),0.0);
}

// based on sdBox()
float sdCheckerboard (in vec2 p, in float b )
{
    float b2 = 2. * b;
    vec2 q = mod(p, b2) - b;
    vec2 r = step(b2, mod(p, 2. * b2));

    // are we inside or outside? (this is either -1. or 1.)
    float side = 2. * (r.y - r.x) * (r.y - r.x) - 1.;

    vec2 d = abs(q) - b;
    return side * max(d.x, d.y);
}

float sdCheckerboard2 (in vec2 p, in float b )
{
    return -min(sdBox(mod(p + b, 4. * b) - 2. * b, b),
                sdBox(mod(p - b, 4. * b) - 2. * b, b));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = 1.8 * (2.0*fragCoord-iResolution.xy)/iResolution.y;

	float d = sdCheckerboard( p, .5);

    float t = .5 * radians(360.) * iTime;
    if (mod(t / radians(360.), 2.) < 1.)
    	d += .2 * sin(t);

    vec3 col = vec3(1.0) - sign(d)*vec3(0.1,0.4,0.7);
	col *= 1.0 - exp(-2.0*abs(d));
	col *= 0.8 + 0.2*cos(140.0*d);
	col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.02,abs(d)) );

	fragColor = vec4(col,1.0);
}
