void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col;
    float t = iTime*.3;
    vec2 uv = (fragCoord-iResolution.xy)/iResolution.y+vec2(t+2.0);
        //uv *= 10.0;
        int c = 0;
        for(int i=0;i<9;i++)
        {
            c = i%3;
            float factor = 1.5;
            vec2 uv1 = uv;
            uv /= factor+col.xy;
            uv += uv1;
            uv += (sin(uv.yx))/factor;
            uv *= factor;
            col[c] += sin(uv.x+uv.y);
        }
    fragColor = vec4(col*100.0,1.0);
}
