void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.x;
    vec2 u = uv;
    float t = iTime * -0.1;
    u += vec2(-.5,-0.2);
    u.x = abs(u.x)+.7;
    u.x = fract(u.x*3.0);
    float a = atan(u.y,u.x)+t;
    float k = fract(a*0.7);
    vec3 ca = vec3(1.2,0.1,-0.3);
    vec3 cb = vec3(.8,0.9,1.29);
    vec3 cc = mix(ca, cb, k);
    if(k>0.5){
      cc.r -= uv.y;
    }
    fragColor = vec4(cc, 1.0);
}
