void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    vec2 uv = fragCoord / iResolution.xy;

    vec2 offset = texture(iChannel1, uv).xz;
    vec2 uvOff = (fragCoord + offset) / iResolution.xy;

    vec4 color = texture(iChannel0, uv);
    vec4 color2 = texture(iChannel0, 1.0 - uvOff);

    fragColor = mod(color / color2, 1.0);
}
