//!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!
//!!!!!!Switch iChannel0 to webcam!!!!!!
//!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!

//change the number of taps
#define taps 12.0

#define tau 6.28

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 c  = texture(iChannel0,uv);
    float t = iTime;
    float d = .02+sin(t)*.02;
    float threshold = 1.0 + sin(t/10.)*sin(t/10.);
    for(float i = 0.; i<tau;i+=tau/taps){
        float a = i+t;

        vec4 c2 = texture(iChannel0,vec2(uv.x+cos(a)*d,uv.y+sin(a)*d));
        if (length(c2) > threshold)
        	c = max(c,c2);
        else
        	c = min(c,c2);
    }
    // No real reason for it, but I'm color deficient, so I'm screwing with your colors.
    fragColor = c;
    fragColor.x += .2*fragColor.y;
    fragColor.y *= .5;
    fragColor.y += .5 * fragColor.x;
}
