#define R iResolution.xy
#define MaxSteps 64.
#define MinDistance 0.01
#define eps 0.0001

float random (in vec2 st) {
    return fract(sin(dot(st.xy,
                         vec2(12.9898,78.233)))*
        43758.5453123);
}

// Based on Morgan McGuire @morgan3d
// https://www.shadertoy.com/view/4dS3Wd
float noise (in vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);

    // Four corners in 2D of a tile
    float a = random(i);
    float b = random(i + vec2(1.0, 0.0));
    float c = random(i + vec2(0.0, 1.0));
    float d = random(i + vec2(1.0, 1.0));

    vec2 u = f * f * (3.0 - 2.0 * f);

    return mix(a, b, u.x) +
            (c - a)* u.y * (1.0 - u.x) +
            (d - b) * u.x * u.y;
}

#define OCTAVES 4
float fbm (in vec2 st) {
    // Initial values
    float value = 0.0;
    float amplitude = .5;
    float frequency = 0.;
    //
    // Loop of octaves
    for (int i = 0; i < OCTAVES; i++) {
        value += amplitude * noise(st);
        st *= 1.5;
        amplitude *= .5;
    }
    return value;
}

mat3 rotateY(float angle) {
	float c = cos(angle), s = sin(angle);
    return mat3(c, 0, s, 0, 1, 0, -s, 0, c);
}

float sdSphere(vec3 p, float r) { return length(p) - r; }
float sdYPlane(vec3 p, float y) { return p.y - y; }
float sdBox(vec3 p, vec3 b) { 
	vec3 d = abs(p) - b;
    return length(max(d,0.0));
}

// Smooth min function from IQ
float smin( float a, float b, float k )
{
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}

float scene(vec3 p) {

    float sphere1 = sdSphere(p - vec3(1,0,0) * cos(iTime), 0.3);
    float sphere2 = sdSphere(p - vec3(0,1,0) * cos(iTime), 0.3);
    float sphere3 = sdSphere(p - vec3(0,0,1) * cos(iTime), 0.3);

    float box = sdBox(p, vec3(.5));
    float outerSphere = sdSphere(p + 0.001*texture(iChannel0, p.xz).xyz, .65);

    box = max(box, outerSphere);

    float res = box;

    float spheres = sphere1;
    spheres = min(spheres, sphere2);
    spheres = min(spheres, sphere3);

    res = smin(spheres, res, .1);

    return res;
}

vec3 calcNormal(vec3 p) {
    float h = 0.0001;
    vec2 k = vec2(1,-1);
    vec3 n = normalize( k.xyy*scene( p + k.xyy*h ) + 
                  k.yyx*scene( p + k.yyx*h ) + 
                  k.yxy*scene( p + k.yxy*h ) + 
                  k.xxx*scene( p + k.xxx*h ) );    
    return n;
}

float march(vec3 ro, vec3 rd) {
    float t = 0., i = 0.;
    for(i=0.; i < MaxSteps; i++) {
    	vec3 p = ro + t * rd;
        float dt = scene(p);
        t += dt;
        if(dt < MinDistance) {
        	return t-MinDistance;  
        }
    }
    return 0.;
}

// https://en.wikipedia.org/wiki/Blinn%E2%80%93Phong_shading_model
vec3 shadeBlinnPhong(vec3 p, vec3 viewDir, vec3 normal, vec3 lightPos, float lightPower, vec3 lightColor) {
    vec3 diffuseColor = vec3(0.5);
    vec3 specColor = vec3(1);
    float shininess = 8.;

    vec3 lightDir = lightPos - p;
    float dist = length(lightDir);
    dist = dist*dist;
    lightDir = normalize(lightDir);

    float lambertian = max(dot(lightDir, normal), 0.0);
    float specular = .0;

    if(lambertian > 0.) {
        viewDir = normalize(-viewDir);

        vec3 halfDir = normalize(viewDir + lightDir);
        float specAngle = max(dot(halfDir, normal), .0);
        specular = pow(specAngle, shininess);
    }

    vec3 color = /*ambientColor +*/
                 diffuseColor * lambertian * lightColor * lightPower / dist +
        		 specColor * specular * lightColor * lightPower / dist;

   	return color;
}

vec3 light(vec3 p, vec3 sn, vec3 rd) {

    mat3 rot = rotateY(iTime);
    vec3 L1 = shadeBlinnPhong(p, rd, sn, rot*vec3(3), 10., vec3(.9,.1,.1));
    vec3 L2 = shadeBlinnPhong(p, rd, sn, rot*vec3(-3), 10., vec3(.9,.1,.1));

    vec3 diffuse = vec3(.1);

    return L1 + L2 + diffuse;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord-R)/R.y;
    vec3 col = vec3(.5,.5,.5);
    vec3 ro = vec3(0,1,-5);
    vec3 rd = normalize(vec3(uv.x, uv.y, 0) - ro);
    mat3 rot = rotateY(-3.1415/3.);
    ro *= rot;
    rd *= rot;

    float t = march(ro, rd);

    if(t > eps) {
        vec3 p = ro + t * rd;
    	vec3 n = calcNormal(p);
        col = light(p, n, rd);
    }

    fragColor = vec4(col,1.0);
}
