float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Ok so UV is the 0.0 - 1.0, 0.0 = 1.0 of the space

    float minRes = iResolution.x;
    if (iResolution.y < minRes) minRes = iResolution.y;

	vec2 uv = fragCoord.xy / minRes;    

    // Define the color it would be if it was 100% succesful
    float r = rand(uv * sin(iTime)), g = rand(uv * cos(iTime)), b = rand(uv * tan(iTime));

    // For reference
    // fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);
    fragColor = vec4(r, g, b, 1.0);
}
